	int jump2(int A[], int n) {
		if (n < 2)
			return 0;
		int level = 0, currentMax = 0, i = 0, nextMax = 0;

		while (currentMax - i + 1 > 0) { //nodes count of current level>0
			level++;
			for (; i <= currentMax; i++) { //traverse current level , and update the max reach of next level
				nextMax = Math.max(nextMax, A[i] + i);
				if (nextMax >= n - 1)
					return level; // if last element is in level+1,  then the min jump=level 
			}
			currentMax = nextMax;
		}
		return 0;
	}

	public int jumpOther(int[] A) {
		int step_count = 0;
		int last_jump_max = 0;
		int current_jump_max = 0;
		for (int i = 0; i < A.length - 1; i++) {
			current_jump_max = Math.max(current_jump_max, i + A[i]);
			if (i == last_jump_max) {
				step_count++;
				last_jump_max = current_jump_max;
			}
		}
		return step_count;
	}

	public int jump(int[] nums) {
		List<LinkedList<Integer>> list = new LinkedList<>();
		for (int i = 0; i < nums.length; i++) {
			LinkedList<Integer> newList = new LinkedList<Integer>();
			list.add(newList);
		}

		int[] record = new int[nums.length];
		for (int i = 1; i < record.length; i++)
			record[i] = Integer.MAX_VALUE;
		int max = 0;

		for (int i = 0; i < nums.length; i++) {
			if (i > max)
				break;
			else {
				max = Math.max(max, nums[i] + i);

				if (i + 1 < nums.length) {
					for (int j = i + 1; j <= nums[i] + i && j < nums.length; j++) {
						list.get(j).add(i);
					}
				}

				for (Integer pos : list.get(i)) {
					record[i] = Math.min(record[i], record[pos] + 1);
				}
			}
		}

//		System.out.println(list);
//		System.out.println(max);

//		for (int i = 0; i < record.length; i++) {
//			System.out.print(record[i] + " ");
//		}
//		System.out.println();

		return record[record.length - 1];
	}
